                    {!values.document_number && "Required"}
                    {values.document_number &&
                      values.document_number.length < 5 &&
                      "ID number must not be below 5 characters."}
                    {values.document_number &&
                      values.document_number.length > 19 &&
                      "ID number must not exceed 19 characters."}
                  </div>
                </div>

