              setLengthOfStayAtAddress(info.value);
            }

            if (info.baseInfo.name.includes("engagingBusiness")) {
              setEngagingBusiness(info.value);
            }

            if (info.baseInfo.name.includes("yearsInBusiness")) {
              setYearsInBusiness(info.value);
